import React from 'react';

const TaskList = ({ tasks, onSelectTask, onCompleteTask, onDeleteTask }) => {
  return (
    <div>
      <h2>Task List</h2>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            <span 
              style={{
                textDecoration: task.completed ? 'line-through' : 'none', // Check if the task is completed
                cursor: 'pointer'
              }}
              onClick={() => onSelectTask(task)} // Ensure onSelectTask is called here
            >
              {task.task} {/* Use task instead of description */}
            </span>
            <button onClick={() => onCompleteTask(task.id)}>
              {task.completed ? 'Undo' : 'Complete'} {/* Modify as necessary based on your app state */}
            </button>
            <button onClick={() => onDeleteTask(task.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskList;
