// Import the Axios library to make HTTP requests
import axios from 'axios';

// Set the base URL for your API (this is where your FastAPI backend will be running)
const API_URL = 'http://localhost:8000/tasks';  // You can change this to the production backend URL later

// Function to fetch tasks from the backend
export const getTasks = async () => {
  try {
    // Making a GET request to the API to fetch the list of tasks
    const response = await axios.get(API_URL);
    // Return the response data (the list of tasks) from the backend
    return response.data;
  } catch (error) {
    // Handle any errors that occur during the API call
    console.error("Error fetching tasks:", error);
    return [];  // Return an empty array if the request fails
  }
};

// Function to create a new task
export const createTask = async (task) => {
  try {
    // Making a POST request to create a new task in the backend
    const response = await axios.post(API_URL, task);
    // Return the created task's data from the backend
    return response.data;
  } catch (error) {
    // Handle any errors that occur during the API call
    console.error("Error creating task:", error);
    return null;  // Return null if the request fails
  }
};

export const updateTask = async (taskId, updatedFields) => {
  try {
    const response = await axios.put(`${API_URL}/${taskId}`, updatedFields);
    return response.data;
  } catch (error) {
    console.error("Error updating task:", error);
    return null;
  }
};

export const deleteTask = async (taskId) => {
  try {
    await axios.delete(`${API_URL}/${taskId}`);
  } catch (error) {
    console.error("Error deleting task:", error);
  }
};
