import React, { useState, useEffect } from 'react';

function TaskList() {
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState("");

    // Fetch tasks when the component mounts
    useEffect(() => {
        fetch('http://localhost:8000/tasks/')  // Replace with the correct API endpoint
            .then(response => response.json())
            .then(data => setTasks(data));
    }, []); // Empty dependency array means it runs only once after mount

    // WebSocket connection for real-time updates
    useEffect(() => {
        const socket = new WebSocket('ws://localhost:8000/ws/tasks');

        socket.onmessage = (event) => {
            console.log("Message from server: ", event.data);
            // Optionally refresh tasks or show a new task message
            fetch('http://localhost:8000/tasks/')  // Fetch updated task list after receiving a new task
                .then(response => response.json())
                .then(data => setTasks(data));  // Update state with new task list
        };

        return () => {
            socket.close();  // Clean up the WebSocket connection when the component is unmounted
        };
    }, []);  // Empty dependency array means it runs only once after mount

    const handleAddTask = () => {
        const task = { task: newTask, completed: false };

        // Make POST request to add task
        fetch('/api/tasks', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(task),
        })
        .then(response => response.json())
        .then(() => {
            // After adding the task, fetch the updated task list
            fetch('http://localhost:8000/tasks/')
                .then(response => response.json())
                .then(data => setTasks(data));  // Update state with new task list
        })
        .catch(error => console.error('Error adding task:', error));
    };

    return (
        <div>
            <input
                type="text"
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
                placeholder="Add a new task"
            />
            <button onClick={handleAddTask}>Add Task</button>

            <ul>
                {tasks.map(task => (
                    <li key={task.id}>{task.task}</li>
                ))}
            </ul>
        </div>
    );
}

export default TaskList;
