// App.js
import React, { useState, useEffect } from 'react';
import TaskInput from './components/TaskInput';
import VoiceInput from './components/VoiceInput';
import TaskList from './components/TaskList';
import TaskDetail from './components/TaskDetail';
import { getTasks, createTask, updateTask, deleteTask } from './services/api';

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [selectedTask, setSelectedTask] = useState(null); // Selected task state
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchTasks = async () => {
      try {
        const data = await getTasks();
        setTasks(data);
      } catch (error) {
        console.error("Error fetching tasks:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchTasks();
  }, []);

  const handleCreateTask = async (taskDescription) => {
    const newTask = { description: taskDescription, completed: false };
    try {
      const createdTask = await createTask(newTask);
      console.log('Created Task:', createdTask);
      if (createdTask) {
        setTasks([...tasks, createdTask]);
      }
    } catch (error) {
      console.error("Error creating task:", error);
    }
  };

  const handleCompleteTask = async (taskId) => {
    try {
      const updatedTask = await updateTask(taskId, { completed: true });
      setTasks(tasks.map(task => (task.id === taskId ? updatedTask : task)));
    } catch (error) {
      console.error("Error completing task:", error);
    }
  };

  const handleDeleteTask = async (taskId) => {
    try {
      await deleteTask(taskId);
      setTasks(tasks.filter(task => task.id !== taskId));
    } catch (error) {
      console.error("Error deleting task:", error);
    }
  };

  return (
    <div>
      <h1>Task Management App</h1>
      <VoiceInput onCreate={handleCreateTask} />
      <TaskInput onCreate={handleCreateTask} />

      {loading ? (
        <p>Loading tasks...</p>
      ) : (
        <div style={{ display: 'flex', gap: '20px' }}>
          <TaskList
            tasks={tasks}
            onSelectTask={setSelectedTask}  // Set selected task here
            onCompleteTask={handleCompleteTask}
            onDeleteTask={handleDeleteTask}
          />
          <TaskDetail task={selectedTask} />  // Display selected task details
        </div>
      )}
    </div>
  );
};

export default App;
