import React, { useState, useEffect } from 'react';
import TaskInput from './components/TaskInput';
import VoiceInput from './components/VoiceInput';
import TaskList from './components/TaskList';
import { getTasks, createTask, updateTask, deleteTask } from './services/api';


const App = () => {
  const [tasks, setTasks] = useState([]);

  // Fetch tasks from the server initially
  useEffect(() => {
    const fetchTasks = async () => {
      const tasks = await getTasks();
      setTasks(tasks);
    };
    fetchTasks();
  }, []);

  // Handle task creation
  const handleCreateTask = async (newTask) => {
    const createdTask = await createTask(newTask);
    if (createdTask) setTasks((prevTasks) => [...prevTasks, createdTask]);
  };

  // Handle task completion
  const handleCompleteTask = async (taskId) => {
    try {
      // Find the task to update based on the taskId
      const taskToUpdate = tasks.find((task) => task.id === taskId);

      // Toggle the 'completed' status of the task
      const updatedTask = { 
        ...taskToUpdate, 
        completed: !taskToUpdate.completed  // Flip the completion status
      };

      // Log the task to ensure completed status is correct
      console.log("Updated Task Payload:", updatedTask);

      // Send a PUT request to the backend to update the task
      await axios.put(`http://localhost:8000/tasks/${taskId}`, updatedTask);

      // Update the local state to reflect the new 'completed' status
      setTasks((prevTasks) =>
        prevTasks.map((task) =>
          task.id === taskId ? { ...task, completed: updatedTask.completed } : task
        )
      );
    } catch (error) {
      console.error("Error completing task:", error);
    }
  };


  // Handle task deletion
  const handleDeleteTask = async (taskId) => {
    const success = await deleteTask(taskId);
    if (success) setTasks((prevTasks) => prevTasks.filter((task) => task.id !== taskId));
  };

  return (
    <div>
      <h1>Task Manager</h1>
      <TaskInput onCreate={handleCreateTask} />
      <VoiceInput onCreate={handleCreateTask} />
      <TaskList
        tasks={tasks}
        onCompleteTask={handleCompleteTask}
        onDeleteTask={handleDeleteTask}
      />
    </div>
  );
};

export default App;
